mapper.insert:
INSERT INTO account(
	account_number,
	client_id,
	account_name,
	deposit,
	account_create_branch,
	account_manager
)
VALUES(
	(SELECT CONCAT(
			(SELECT 
				branch_code
			FROM branch 
			WHERE branch_name = #{accountCreateBranch}
			),
			(SELECT EXTRACT(
				DAY_SECOND FROM now()
				)
			)
	)
	),
	#{clientId},
	#{accountName},
	#{deposit},
	#{accountCreateBranch},
	#{accountManager}
)
