mapper.insert:
INSERT INTO order(
	order_day,
	order_number,
	account_number,
	client_id,
	item_code,
	order_count,
	order_pay 
)
VALUES(
	NOW(),
	(SELECT CONCAT(
				(SELECT 
					branch_code
				FROM branch 
				WHERE branch_name = #{accountCreateBranch}
				),
				(SELECT EXTRACT(
					DAY_SECOND FROM now()
					)
				)
		)
		),
	#{accountNumber},
	#{clientId},
	#{itemCode},
	#{orderCount},
	#{orderPay}
)
